 <style type="text/css">.form-group{      margin-top: 5px;}.box-v4 .panel-heading {    padding: 7px;}.box-v1 .panel-body {    padding-top: 0px;}.box-v3 {    padding: 0px;}.panel {    margin-bottom: 6px; } .box-v4 .panel-body .box-v4-alert{    width: 100%;    padding: 0px;  }  .table.table-bordered thead th, table.table-bordered thead td {    border-left-width: 0;    border-top-width: 0;    background-color: #ff001b;    color: #fff;}.col-md-2{  position: relative;min-height: 1px;padding-right: 15px;padding-left: 9px;}  .col-md-2-v1{    position: relative;    min-height: 1px;    padding-right: 10px;    padding-left: 10px;    width: 14%;    float: left;  }   .col-md-2-v2{    position: relative;    min-height: 1px;    padding-right: 10px;    padding-left: 10px;    width: 9%;    float: left;  }  .label-v1 {    display: inline-block;    max-width: 100%;    margin-bottom: 5px;    font-weight: 700;    margin-left: 20px;}</style><div id="content">           <div class="col-md-12" style="padding:20px;">                    <div class="col-md-12 padding-0"><div class="col-md-12 padding-0">                            <div class="col-md-12">                                <div class="panel box-v4">                                    <div class="panel-heading bg-white border-none">              <h4><span class="fa fa-cart-arrow-down"></span> Register Barang Baru</h4>                                    </div>                                    <div class="panel-body padding-0">       <div class="col-md-12 col-xs-12 col-md-12 padding-0 box-v4-alert">        <form  id="form_transaksi" role="form">          <div class="col-md-10">            <div class="form-group">           <label class="control-label col-md-2" >Kode Barang :</label>              <div class="col-md-2">                <input type="text" class="form-control reset" name="kode_produk" id="kode_produk" required placeholder="Max 12 Kombinasi">              </div>              <label class="control-label col-md-2-v1" for="nama_barang">Nama Barang :</label>              <div class="col-md-4">                <input type="text" class="form-control reset" name="nama_barang" id="nama_barang">              </div>          </div>          </div>        </form><div class="col-md-5">   <button type="button" class="btn btn-primary" id="tambah" onclick="addBarang()">          <i class="fa fa-cart-plus"></i> Tambah</button>        </div>    </div></div></div></div></div></div>     <!--  <div class="panel-body">        <table id="table_transaksi" class="table table-striped table-bordered">        <thead>          <tr>              <th>No</th>              <th>Nama Barang</th>              <th>Aksi</th>          </tr>        </thead>        <tbody>        </tbody>      </table>      <div class="col-md-offset-8" style="margin-top:20px">        <button type="button" class="btn btn-primary btn-lg" id="selesai" disabled="disabled" onclick="selesai();">        Selesai <i class="fa fa-angle-double-right"></i></button>      </div>                    </div> -->       </div>       </div>       </div>       </div>       </div>       </div>       </div><script type="text/javascript">var table;    $(document).ready(function() {      table = $('#table_transaksi').DataTable({         paging: false,        "info": false,        "searching": false,        "processing": true, //Feature control the processing indicator.        "serverSide": true, //Feature control DataTables'         // server-side processing mode.                // Load data for the table's content from an Ajax source        "ajax": {            "url": "<?= site_url('pembelian/ajax_list_transaksi_barangbaru')?>",            "type": "POST"        },        //Set column definition initialisation properties.        "columnDefs": [        {           "targets": [ 0,1,2], //last column          "orderable": false, //set not orderable        },        ],      });    });function showSelesai(str){  if(str !== ''){    $('#selesai').removeAttr("disabled");  }else{      $('#selesai').attr("disabled","disabled");  }}function selesai(){    $.post("<?= site_url('pembelian/registerBarang')?>", //Required URL of the page on server            { // Data Sending With Request To Server              id:0            },            function(response,status){ // Required Callback Function                alert("*----Received Data----*\n\nResponse : " + response+"\n\nStatus : " + status);                location.reload();            });  } function reload_table()    {      table.ajax.reload(null,false); //reload datatable ajax     }function addBarang(){  var id_barang   = $("#kode_produk").val();  var nama_barang = $("#nama_barang").val();    if (nama_barang == '') {          $('#nama_barang').focus();        }else if(id_barang == ''){          $('#kode_produk').focus();        }else{       // ajax adding data to database        //   $.ajax({        //     url : "<?= site_url('pembelian/tambahbarang')?>",        //     type: "POST",        //     data: $('#form_transaksi').serialize(),        //     dataType: "JSON",        //     success: function(response,data)        //     {        //       reload_table();          //       console.log(response);        //     },        //     error: function (jqXHR, textStatus, errorThrown)        //     {        //         alert('Error adding data'+textStatus+jqXHR+errorThrown);        //     }        // });          $.post("<?= site_url('pembelian/registerBarang')?>", //Required URL of the page on server            { // Data Sending With Request To Server              id:id_barang,              nama:nama_barang            },            function(response,status){ // Required Callback Function                alert("*----Received Data----*\n\nResponse : " + response+"\n\nStatus : " + status);                // location.reload();            });            // showTotal();            $('.reset').val('');        }}function deletebarang(id)    {        // ajax delete data to database          $.ajax({            url : "<?= site_url('pembelian/deletebarangbaru')?>/"+id,            type: "POST",            dataType: "JSON",            success: function(data)            {               reload_table();            },            error: function (jqXHR, textStatus, errorThrown)            {                alert('Error deleting data');            }        });    }</script>